query.sql:
declare
c clob;
v varchar2(50);
vBuffer    VARCHAR2 (32767);
tmp_amount   BINARY_INTEGER := 0;
l_b_pos      PLS_INTEGER := 1;
l_e_pos      PLS_INTEGER := 1;
l_cnt      PLS_INTEGER := 1;
l_clob_len PLS_INTEGER;
begin
select :SQL_ID into v from dual;
SELECT DBMS_SQLTUNE.report_sql_monitor(
  sql_id       => v,
  type         => 'TEXT',
  report_level => 'ALL') AS report into c
FROM dual;
dbms_output.enable(500000000);
dbms_output.put_line('<PRE>');

  l_clob_len := dbms_lob.getlength(c);  
  LOOP      
    l_cnt := l_cnt + 1;    
    l_e_pos := dbms_lob.INSTR(c, chr(10), 1, l_cnt);    
    EXIT WHEN (l_e_pos = 0);
    tmp_amount := l_e_pos-l_b_pos;        
    if tmp_amount = 0 then tmp_amount := 1; end if;
    dbms_lob.read(c, tmp_amount, l_b_pos, vBuffer);
    vBuffer := replace(vBuffer, chr(10));
    dbms_output.put_line(vBuffer);
     l_b_pos := l_e_pos; 
  END LOOP;

  tmp_amount := l_clob_len-l_b_pos;
  dbms_lob.read(c, tmp_amount, l_b_pos, vBuffer);
  vBuffer := replace(vBuffer, chr(10));
  dbms_output.put_line(vBuffer);  
  dbms_output.put_line('</PRE>');
exception when others then 
  dbms_output.put_line('error execute report : ' || sqlerrm);
  dbms_output.put_line('</PRE>');
end;

declare
--c clob;
v varchar2(50);
begin
 select :SQL_ID into v from dual;
dbms_output.enable(5000000);
dbms_output.put_line('<PRE>');
 for i in (SELECT plan_table_output FROM table(DBMS_XPLAN.DISPLAY_AWR(v)))
 loop
dbms_output.put_line(i.plan_table_output);
end loop;
dbms_output.put_line('</PRE>');
end;

declare 
TID varchar2(200);
err varchar2(2000);
begin
TID:=:DTI;
select repadmin.show_err_mm('DTI',1) into err from dual;
dbms_output.put_line(TID);
dbms_output.put_line(err);
end;

declare
--c clob;
v varchar2(50);
begin
 select :SQLID into v from dual;
dbms_output.enable(5000000);
dbms_output.put_line('<PRE>');
 for i in (SELECT plan_table_output FROM table(DBMS_XPLAN.DISPLAY_AWR(sql_id =>v,format=>'+OUTLINE')))
 loop
dbms_output.put_line(i.plan_table_output);
end loop;
dbms_output.put_line('</PRE>');
end;

DECLARE
ObjList dbms_stats.ObjectTab;
BEGIN
DBMS_STATS.GATHER_SCHEMA_STATS(ownname=>:SCHEMA, objlist=>ObjList, options=>'LIST STALE');
FOR i in ObjList.FIRST..ObjList.LAST
LOOP
dbms_output.put_line(ObjList(i).ownname || '.' || ObjList(i).ObjName || ' ' || ObjList(i).ObjType || ' ' || ObjList(i).partname);
END LOOP;
END;
